import React, { useEffect, useState } from 'react';
import { Outlet } from 'react-router';
import Container from 'react-bootstrap/Container'
import { Link, useNavigate } from "react-router-dom";
import Navbar from 'react-bootstrap/Navbar'
import Nav from 'react-bootstrap/Nav'
import Button from 'react-bootstrap/Button';
import Spinner from 'react-bootstrap/Spinner';
import util from "../util.js";
import styled from 'styled-components';

const StyledButton = styled(Button)`
  background-color: #2c4b9c;
  color: white;
`;

function Navigation() {
  const navigate = useNavigate();
  const [data, setData] = useState('/');
  const [signOutLoading, setSignOutLoading] = useState(false);

  function signOutRequest() {
    setSignOutLoading(true);
    util.requestPOST('/causes_api/signout', "")
      .then(res => {
        setSignOutLoading(false);
        navigate('/');
      });
  }

  useEffect(() => {
    util.requestGET('/causes_api/get_user').then(res => {
      if (res.status === 200) {
        res.json().then(body => {
          if ('volunteer' in body) {
            setData('/volhome');
          } else {
            setData('/orghome')
          }
        });
      }
    });
  });
  return (
    <>
      <div className="Navigation">
        <Container style={{maxWidth: 1200}}>
        <Navbar expand="md" variant="light">
            <Container>
            <Link to="/" className="navbar-brand" style={{color: "#2c4b9c"}}><h3>Team Pika</h3></Link>
            <Navbar.Toggle aria-controls="responsive-navbar-nav" />
            <Navbar.Collapse id="responsive-navbar-nav">
                <Nav className="flex-grow-1 pe-3" fill>
                <Nav.Item>
                <Link className="nav-link" to="/"><h4>Home</h4></Link>
                </Nav.Item>
                <Nav.Item>
                <Link className="nav-link" to="/search"><h4>Search</h4></Link>
                </Nav.Item>
                <Nav.Item className="me-auto">
                  {!signOutLoading &&
                    <StyledButton variant="light" size="sm" onClick={signOutRequest}><h4>Sign Out</h4></StyledButton>
                    }
                    {signOutLoading &&
                    <Spinner animation="border" variant="primary" />
                    }
                </Nav.Item>
                </Nav>
            </Navbar.Collapse>
            </Container>
        </Navbar>
        </Container>
        </div>
      <Outlet />
    </>
  );
};

export default Navigation;
