import React, { useEffect, useState } from 'react';
import { Outlet } from 'react-router';
import Container from 'react-bootstrap/Container'
import { Link, useNavigate } from "react-router-dom";
import Navbar from 'react-bootstrap/Navbar'
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col'
import Nav from 'react-bootstrap/Nav'
import Button from 'react-bootstrap/Button';
import Spinner from 'react-bootstrap/Spinner';
import util from "../util.js";
import styled from 'styled-components';

const StyledButton = styled(Button)`
  background-color: #2c4b9c;
  color: white;
`;

function Navigation() {
  const navigate = useNavigate();
  const [data, setData] = useState('/');
  const [signOutLoading, setSignOutLoading] = useState(false);

  function signOutRequest() {
    setSignOutLoading(true);
    util.requestPOST('/causes_api/signout', "")
      .then(res => {
        setSignOutLoading(false);
        navigate('/');
      });
  }

  useEffect(() => {
    util.requestGET('/causes_api/get_user').then(res => {
      if (res.status === 200) {
        res.json().then(body => {
          if ('volunteer' in body) {
            setData('/volhome');
          } else {
            setData('/orghome')
          }
        });
      }
    });
  });
  return (
    <>
      <div className="Navigation">
        <Container fluid style={{maxWidth: 1200}}>
        <Navbar expand="md" variant="light">
            <Container>
            <Link to="/" className="navbar-brand"><h3>Team Pika</h3></Link>
            <Navbar.Toggle aria-controls="responsive-navbar-nav" />
            <Navbar.Collapse id="responsive-navbar-nav">
                {/* <Nav className="justify-content-end">
                <Row>
                <Col>
                <Link className="nav-link" to="/"><h4>Home</h4></Link>
                </Col>
                <Col>
                <Link className="nav-link" to="/search"><h4>Search Organizations</h4></Link>
                </Col>
                <Col>
                  {!signOutLoading &&
                    <StyledButton variant="light" size="sm" onClick={signOutRequest}><h4>Sign Out</h4></StyledButton>
                    }
                    {signOutLoading &&
                    <Spinner animation="border" variant="primary" />
                    }
                  </Col>
                  </Row>
                </Nav> */}
                <Nav clasName="ms-auto">
                <Link className="nav-link" to="/"><h4>Home</h4></Link>
                <Link className="nav-link" to="/search"><h4>Search Organizations</h4></Link>
                  {!signOutLoading &&
                    <StyledButton variant="light" size="sm" onClick={signOutRequest}><h4>Sign Out</h4></StyledButton>
                    }
                    {signOutLoading &&
                    <Spinner animation="border" variant="primary" />
                    }
                </Nav>
            </Navbar.Collapse>
            </Container>
        </Navbar>
        </Container>
        </div>
      <Outlet />
    </>
  );
};

export default Navigation;
