import React, { useState } from "react";
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import Spinner from 'react-bootstrap/Spinner';
import { Link, useNavigate } from "react-router-dom";
import util from '../util.js';

function SignUpVol() {
  const [loading, setLoading] = useState(false);

  const navigate = useNavigate();

  function formSubmit(e) {
    e.preventDefault();
    setLoading(true);
    const formData = new FormData(e.target);
    util.requestPOST('/causes_api/signupvol', formData)
      .then(res => {
        if (res.status === 200) {
          navigate('/volhome');
        } else {
          res.text().then(data => {
            /* TODO: the next line should be changed to display a message
            to the user that the data incorrect. Could use
            the variable "data" for that. */
            alert(`Some error! (${data}) TODO: handle this (see signupvol.jsx)`);
          });
        }
        setLoading(false);
      });
  }

  return (
    <div className="body">
      <Card style={{ width: '600px' }} className = "mx-auto my-5">
      <Card.Body>
      <Card.Title>Volunteer Sign Up</Card.Title>
      <Card.Text style={{textAlign: "left"}}>
      <Form onSubmit={formSubmit}>

        <Form.Group>
          <Form.Label>Name</Form.Label>
          <Form.Control placeholder="Enter your name" name="name" />
        </Form.Group>

        <Form.Group>
          <Form.Label>Bio</Form.Label>
          <Form.Control placeholder="Enter your bio" name="bio" />
        </Form.Group>

        <Form.Group className="mb-3" controlId="formBasicEmail">
          <Form.Label>Email address</Form.Label>
          <Form.Control type="email" placeholder="Enter your email" name="email" />
          <Form.Text className="text-muted">
            We'll never share your email with anyone else.
          </Form.Text>
        </Form.Group>

        <Form.Group>
          <Form.Label>Account Username</Form.Label>
          <Form.Control placeholder="Enter a username" name="username" />
        </Form.Group>

        <Form.Group className="mb-3" controlId="formBasicPassword">
          <Form.Label>Account Password</Form.Label>
          <Form.Control type="password" placeholder="Enter a password" name="password" />
        </Form.Group>

        {!loading &&
          <Button variant="outline-secondary" type="submit">
            Create a volunteer account
          </Button>
        }
        {loading &&
          <Spinner animation="border" variant="primary" />
        }
        
      </Form>
      </Card.Text>
      <span>Already have an account? </span>
      <Link to="/">Sign In </Link>
      </Card.Body>
      </Card>
    </div>
  );
}

export default SignUpVol;
