import React, { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import Spinner from 'react-bootstrap/Spinner';
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col'
import Button from 'react-bootstrap/Button'
import { NotFound } from ".";
import util from "../util";

function EventHome() {
  const [eventData, setEventData] = useState(null);
  const [createTask, setCreateTask] = useState(false);
  const [error, setError] = useState(null);
  const [signUp, setSignUp] = useState(null);
  const [loaded, setLoaded] = useState(false);

  const navigate = useNavigate();

  useEffect(() => {
    renderEvent();
  });

  /* function getQueryParams
   *
   * Returns a JavaScript object containing
   *   'org', the name of the organization that organizes this event
   *   'event', the name of the event
   */
  function getQueryParams() {
    const params = new Proxy(new URLSearchParams(window.location.search), {
      get: (searchParams, prop) => searchParams.get(prop),
    });

    const orgParam = params.org;
    const eventParam = params.event;

    const queryParams = {
      'org' : orgParam,
      'event' : eventParam
    };

    return queryParams;
  }

  /* function handleError
   *
   * Takes action based on a request to the server returning an error.
   * If the user is unauthorized, redirects to '/'. Otherwise, displays
   * the 404 error page.
   * Argument "response" should be a received HTTP response.
   */
  // function handleError(response) {
  //   if (response.status === 401) {
  //     navigate('/');
  //   } else {
  //     setError(<NotFound />);
  //   }
  // }

  /* function fetchEventData
   *
   * Makes an HTTP request to fetch event data from the backend.
   *
   * Argument "queryParams" should be a JavaScript object containing
   * 'org', the name of the organization that organizes this event
   * 'event', the name of the event.
   *
   * On success, returns a Promise that is a JavaScript object containing
   * 'eventName', the name of the event
   * 'orgName', the name of the organization that organizes the event
   * 'category', the category of the event
   * 'date', the date of the event
   * 'bio', the description of the event
   *
   * On error, returns a Promise that contains null. In the event of an error,
   * fetchEventData takes appropriate action to handle the error.
   */
  // async function fetchEventData(queryParams) {
  //   const response = await util.requestGET(
  //     '/causes_api/get_event', queryParams, 'application/json'
  //   );

  //   if (response.status === 200) {
  //     const body = await response.json();
  //     return body;
  //   } else {
  //     handleError(response);
  //     return null;
  //   }
  // }

  /* function renderEvent
   *
   * Fetches and displays the correct event. Parses the organization name
   * and event name from the query parameters, fetches the event data,
   * and constructs the JSX for the event. Finally, calls setData() to
   * display the event.
   *
   * Both organization and event must be specified in the query parameters
   * for this page.
   */
  function renderEvent() {
    // do not re-render if data already rendered
    if (eventData != null || error != null)
      return;

    setEventData(false);
    setLoaded(true);

    // need to add function that checks 
    // if the org page is it's own home page
    // or another org
    setCreateTask(true)

    const queryParams = getQueryParams();

    if (queryParams['org'] == null || queryParams['event'] == null) {
      // Both org and event needed to find resource
      // setLoaded(true);
      setError(<NotFound />);
      return;
    }

  //   fetchEventData(queryParams).then(eventData => {
  //     setLoaded(true);
  //     if (eventData == null) {
  //       // Something went wrong.
  //       return;

    util.requestGET(
      '/causes_api/get_event', queryParams, 'application/json'
    ).then(res => {
      if (res.status === 200) {
        res.json().then(body => {
          // const orgData = body['org'];
          const orgEvent = body['event_info'];
          const user = body['user'];

          setEventData(orgEvent);

          if (queryParams['org'] == null) {
            // We are displaying logged-in organization
            // setCreateTask(false);
          } else if ('volunteer' in user) {
            setSignUp(!user.volunteer.orgs.includes(queryParams['org']));
          }
        });
      } else if (res.status === 401) {
        navigate('/');
      } else {
        setError(404);
      }
       
  //     setEventData(eventData);
    });
  }

   /* function followVol
   *
   * Makes request to backend to sign logged-in volunteer user up
   * for this event.
   */
   function goingVol() {
    const queryParams = getQueryParams();
    const body = JSON.stringify({'event' : queryParams['event']});

    util.requestPOST('/causes_api/vol_event_link', body).then(res => {
      if (res.status === 200) {
        setSignUp(false);
      } else {
        // TODO: change this
        alert("Something went wrong.");
      }
    });
  }

  function notGoingVol() {
    const queryParams = getQueryParams();
    const body = JSON.stringify({'event' : queryParams['event']});

    util.requestPOST('/causes_api/vol_event_unlink', body).then(res => {
      if (res.status === 200) {
        setSignUp(true);
      } else {
        // TODO: change this
        alert("Something went wrong.");
      }
    });
  }

  return (
    <div className="home">
      {eventData &&
        <div className="eventinfo">
          <h1>{ eventData.eventName }</h1>
          <h4>Organized by { eventData.orgName }</h4>
          <h3>{ eventData.date }</h3>
          <Row>
            <Col style={{textAlign: 'left' }}>
            <h5>{ eventData.category }</h5>
            </Col>
            <Col style={{textAlign: 'right' }}>
              {signUp &&
                <Button variant="outline-secondary" style={{marginTop: 15 }} onClick={goingVol}>
                  Going
                </Button>
              }
              {signUp === false &&
                <Button variant="outline-secondary" style={{marginTop: 15 }} onClick={notGoingVol}>
                Not Going
              </Button>
              }
            </Col>
          </Row>
          
          <p style={{textAlign: 'left' }}>{ eventData.bio }</p>
          <Row className ="my-5">
            <Col style={{textAlign: 'left' }}>
            <h3>Tasks</h3>
            </Col>
            <Col style={{textAlign: 'right' }}>
            {createTask && (<Link to="/createtask">
            <Button variant="outline-secondary" type="submit" style={{marginTop: 15 }}>
                + Add Task
            </Button>
            </Link>)}
            </Col>
          </Row>

        </div>
      }
      {!loaded &&
        <div className="my-3">
          <Spinner animation="border" variant="primary" />
        </div>
      }
      {error}
    </div>
  );
}

export default EventHome;
